import React from 'react'
import './Testimonials.css'
import { Swiper, SwiperSlide } from 'swiper/react'
import profilePic1 from '../../img/profile1.jpg'
import profilePic2 from '../../img/profile2.jpg'
import profilePic3 from '../../img/profile3.jpg'
import profilePic4 from '../../img/profile4.jpg'
import Steve from '../../img/Steve.jpg'
import Bill from '../../img/Bill.jpg'
import Elon from '../../img/Elon.jpg'
import Debjani from '../../img/Debjani.jpg'
import { Pagination } from 'swiper'
import 'swiper/css/pagination'
import 'swiper/css'

const Testimonials = () => {
    const clients = [
        {
            img: Steve,
            review1: " 'Because the people who are crazy enough to think they can change the world are the ones who do.'",
            review2: "Steve Jobs"
        },
        {
            img: Bill,
            review1: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Rem pariatur iure assumenda eligendi voluptates nemo atque nisi quae minima repellat, numquam quibusdam officiis autem, laboriosam soluta aperiam repudiandae iusto commodi.",
            review2: "Steve Jobs"
        },
        {
            img: Elon,
            review1: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Rem pariatur iure assumenda eligendi voluptates nemo atque nisi quae minima repellat, numquam quibusdam officiis autem, laboriosam soluta aperiam repudiandae iusto commodi.",
            review2: "Steve Jobs"
        },
        {
            img: Debjani,
            review1: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Rem pariatur iure assumenda eligendi voluptates nemo atque nisi quae minima repellat, numquam quibusdam officiis autem, laboriosam soluta aperiam repudiandae iusto commodi.",
            review2: "Steve Jobs"
        }
    ]
    return (
        <div className="t-wrapper" id='Testimonials'>
            <div className="t-heading">
                <span>Clients always get </span>
                <span>Exceptional Work </span>
                <span>from me...</span>
                <div className="blur t-blur1" style={{ background: "var(--purple)" }}></div>
                <div className="blur t-blur2" style={{ background: "skyblue" }}></div>
            </div>
            {/*Slider */}
            <Swiper
                modules={[Pagination]}
                slidesPerView={1}
                pagination={{ clickable: true }}
            >
                {clients.map((client, index) => {
                    return (
                        <SwiperSlide key={index}>
                            <div className="testimonial">
                                <img src={client.img} alt="" />
                                <span><i>{client.review1}</i></span>
                                <span>{client.review2}</span>
                            </div>

                        </SwiperSlide>
                    );
                }
                )}
            </Swiper>
        </div>
    )
}

export default Testimonials
